mapper.select:
select * from comment_metadata


		c_idx=#{c_idx}


		and m_idx = #{m_idx}


		and m_nick = #{m_nick}


		and c_contents = #{c_contents}


		and c_img = #{c_img}


		and c_grade = #{c_grade}


		and rcp_idx = #{rcp_idx}


		and c_status = #{c_status}


		and c_time = #{c_time}


		and c_blind = #{c_blind}


		and c_like = #{c_like}

mapper.update:
update comment
set c_contents = #{newText}
where c_idx = #{c_idx}
